/**
 * ------- UTILIDADES -------
 */

// Converte ‚Äú29/04/2025 09:00‚Äù  ->  ‚Äú2025-04-29T09:00:00‚Äù
const parseBrDate = (s) => {
  if (!s) return null;
  const [d, m, rest] = s.split('/');
  const [y, time]    = rest.trim().split(' ');
  const [hh, mm]     = time.split(':');
  return `${y}-${m.padStart(2,'0')}-${d.padStart(2,'0')}T${hh.padStart(2,'0')}:${mm.padStart(2,'0')}:00`;
};

// Garante formato ISO e coloca ‚Äú:00‚Äù se segundos estiverem ausentes
const cleanDateString = (dateStr) => {
  if (!dateStr) return null;
  dateStr = dateStr.replace(' ', 'T')        // troca espa√ßo por T
                   .replace(/Z$/, '')        // remove Z final
                   .split('.')[0];           // remove milissegundos
  if (dateStr.split(':').length === 2) dateStr += ':00'; // adiciona segundos
  return dateStr;
};

// Soma +1 hora (rodaÃÇ 30 => 00)
const addOneHour = (dateStr) => {
  const [datePart, timePart] = dateStr.split('T');
  let [hh, mm, ss] = timePart.split(':').map(Number);

  hh = (hh + 1) % 24;                         // 23+1 ‚Üí 0
  return `${datePart}T${String(hh).padStart(2,'0')}:${String(mm).padStart(2,'0')}:${String(ss).padStart(2,'0')}`;
};

// Diferen√ßa (em horas) entre dois ISO strings (sem ms)
const getHourDifference = (startStr, finishStr) => {
  const start = new Date(startStr);
  const finish = new Date(finishStr);
  return (finish - start) / (1000 * 60 * 60); // ms ‚Üí h
};

/**
 * ------- PROCESSAMENTO -------
 */

