let dateFinish = item.json.date_finish;  // idem
let dataAntiga = item.json.data_antiga;  // opcional

// 2. Caso venham no formato BR (dd/mm/aaaa), converte primeiro
if (dateStart?.includes('/') && dateStart?.includes(' '))  dateStart  = parseBrDate(dateStart);
if (dateFinish?.includes('/') && dateFinish?.includes(' ')) dateFinish = parseBrDate(dateFinish);

// 3. Padroniza e garante segundos
dateStart  = cleanDateString(dateStart);
dateFinish = cleanDateString(dateFinish);
dataAntiga = cleanDateString(dataAntiga);

// 4. Se tiver start & finish mas eles NAÃÉO differem 1h ‚Üí corrige finish
if (dateStart && dateFinish) {
  const diff = getHourDifference(dateStart, dateFinish);
  if (diff !== 1) dateFinish = addOneHour(dateStart);
}

// 5. Escreve de volta no item
