// Este código extrai dados de clínicas de estética de um JSON fornecido pelo SerpAPI
// e retorna um array de objetos no formato esperado pelo N8N

// Código para n8n deve retornar itens usando a variável items
// Função que será executada a cada item que entra no nó
function processData(item) {
  try {
    // Obter os dados do JSON diretamente do item
    const jsonData = $("Busca na SerpAPI").first().json;

    // Verificar se temos a estrutura necessária
    if (!jsonData || !jsonData.local_results || !Array.isArray(jsonData.local_results)) {
      // Se não encontrar local_results, verificar se é um array
      if (Array.isArray(jsonData) && jsonData[0] && jsonData[0].local_results) {
        // Caso seja um array com o primeiro elemento contendo local_results
        return extractClinicData(jsonData[0].local_results);
      } else {
        // Tentar encontrar os dados em outras estruturas possíveis
        const possibleData = findLocalResults(jsonData);
        if (possibleData) {
          return extractClinicData(possibleData);
        }

        // Se nada funcionar, retornar um erro como item
        return [{
          error: true,
          message: 'Estrutura de dados inválida: local_results não encontrado',
          originalData: jsonData
        }];
      }
    }

    // Se chegou aqui, encontrou local_results diretamente
    return extractClinicData(jsonData.local_results);

  } catch (error) {
    // Em caso de erro, retornar um item com informações do erro
    return [{
      error: true,
      message: `Erro ao processar dados: ${error.message}`,
      stack: error.stack
    }];
  }
}

// Função para encontrar local_results em diferentes níveis da estrutura
function findLocalResults(data) {
  // Se for um objeto, verificar suas propriedades
  if (data && typeof data === 'object' && !Array.isArray(data)) {
    // Verificar se tem local_results
    if (data.local_results && Array.isArray(data.local_results)) {
      return data.local_results;
    }

    // Verificar em cada propriedade
    for (const key in data) {
      const result = findLocalResults(data[key]);
      if (result) return result;
    }
  }

  // Se for um array, verificar cada elemento
  if (Array.isArray(data)) {
    for (const item of data) {
      const result = findLocalResults(item);
      if (result) return result;
    }
  }

  return null;
}

// Função para extrair e formatar os dados das clínicas
function extractClinicData(localResults) {
  return localResults.map(clinica => ({
    name: clinica.title || '',
    endereco: clinica.address || '',
    telefone: clinica.phone ? clinica.phone.replace(/[^\d]/g, '') : '',
    rating: clinica.rating || '',
    reviews: clinica.reviews || '', 
    website: clinica.website || '',
    // Adicionar o campo types como uma string separada por vírgulas
    types: Array.isArray(clinica.types) ? clinica.types.join(', ') : ''
  }));
}

// Processar todos os itens de entrada
// Esta é a forma que o N8N espera receber os resultados
return items.map(processData).flat();
